import React, { Component } from 'react'
import {storeProducts, detailProduct} from './data'

const ProductContext = React.createContext()

class ProductProvider extends Component {

    

    state = {
        products: [], 
        detailProduct: detailProduct,
        cart: [],
        cartTax:0,
        cartTotal:0
    }


    updateCart(){
        let cart = JSON.parse(localStorage.getItem("Cart"));
        if (cart != null){
            return cart
        }
        return [];
        
    }

    updateProducts(){
        let product = JSON.parse(localStorage.getItem("Products"));
        if (product != null){
            return product
        }
        return [];
        
    }

    componentDidMount() {
        if (this.state.products.length == 0){
            this.setProducts();
            console.log("products was empty");
        }

        const inCart = this.updateCart();

        this.setState(()=>{
            return {cart:[...inCart]};
        },()=>{ 
            this.addTotals();
            localStorage.setItem('Cart',JSON.stringify(this.state.cart));
            localStorage.setItem('Products', JSON.stringify(this.state.products));
            
        });
    }


    componentDidUpdate(prevProps, prevState) {
        if (this.state !== prevState) {
            localStorage.setItem('Cart',JSON.stringify(this.state.cart));
            localStorage.setItem('Products', JSON.stringify(this.state.products));
        }
      }
    

    setProducts = () =>{
        let tempProducts = [];
        storeProducts.forEach(item =>{
            const singleItem = {...item};
            tempProducts = [...tempProducts,singleItem];
        });
        this.setState(()=>{
            return {products: tempProducts}
        });
        
    }



    getItem = (id) =>{
        const product = this.state.products.find(item => item.id === id);
        //console.log(product + "wow" + this.state.products);
        return product; 
    }

    getItemAlt = (id) =>{
        const products = this.updateProducts();
        const product = products.find(item => item.id === id);
        console.log(products + "wow somehthing:" + product);
        return product; 
    }

    handleDetail = (id) =>{ 
        const product = this.getItem(id);
        this.setState(()=>{
            return {detailProduct:product}
        })        
    }
    
    addToCart = id =>{

        const inCart = this.updateCart();
 
        const products = this.updateProducts();

        
        
        let tempProducts = [...this.state.products];   
        const index = tempProducts.indexOf(this.getItem(id));
        
        

        const wait = products.filter(item => item.id == id);
        const temp = tempProducts.filter(item => item.id != id);

        //console.log(wait);
        //console.log(tempProducts);
        
        const product = wait[0];

        product.inCart = true;
        product.count = 1;
        const price = product.price;
        product.total = price;

        wait[0] = product;
        console.log(temp);
        console.log(wait);
        console.log(tempProducts);
        tempProducts = [...temp,...wait];

        this.setState(()=>{
            return {products:[...tempProducts],cart:[...inCart,product]};
        },()=>{ 
            this.addTotals();
            localStorage.setItem('Cart',JSON.stringify(this.state.cart));
            localStorage.setItem('Products', JSON.stringify(this.state.products));
            // console.log(this.state.cart);
        });

       
    };
         
    
    
    increment = (id) =>{
        console.log('this is an increment')

    }

    decrement = (id) =>{
        console.log('this is an Decrement')
        
    }
    
    removeItem = (id) => {
        let tempProducts = [...this.state.products];
        let tempCart = [...this.state.cart];

        tempCart = tempCart.filter(item => item.id !== id);
        
        const index = tempProducts.indexOf(this.getItem(id));
        let removedProduct = tempProducts[index];
        removedProduct.inCart = false;
        removedProduct.count = 0;
        removedProduct.total = 0;

        this.setState(()=>{
            return {
                cart:[...tempCart],
                products:[...tempProducts],    
            }
        },
        () => {
            this.addTotals();
            localStorage.setItem('State',JSON.stringify(this.state));
        }
        )
    }

    clearCart = () =>{
        this.setState(()=>{
            return {cart:[]};
        },()=>{ 
            this.addTotals();
            localStorage.setItem('Cart',JSON.stringify(this.state.cart));
            
        });
    }

    addTotals = () =>{
        let subTotal = 0;
        this.state.cart.map(item => (subTotal += item.total));
        const tempTax = subTotal * 0.1;
        const tax = parseFloat(tempTax.toFixed(2));
        const total = subTotal + tax
        this.setState (()=>{
            return {
                cartSubTotal: subTotal,
                cartTax: tax,
                cartTotal:total
            }
            },() => {
                
            }
        );
    }

    render() {
                return (
                    <ProductContext.Provider value = {{
                        ...this.state,
                        handleDetail: this.handleDetail,
                        addToCart: this.addToCart,
                        increment: this.increment,
                        decrement: this.decrement,
                        removeItem: this.removeItem,
                        clearCart: this.clearCart
                    }}>
                    {this.props.children} 
                    </ProductContext.Provider>
                )
        }
}

const ProductConsumer = ProductContext.Consumer;

export {ProductProvider, ProductConsumer};
